mapper.select:
SELECT s.ClassId AS classid, r.RouteName AS routename, s.ClassName AS classname, s.StartingTimeOfShift AS startingtimeofshift, s.ClosingTimeOfShift AS closingtimeofshift, s.PatrolDuration AS patrolduration, s.PatrolIntervalLength AS patrolintervallength, s.ClassErrorRange AS classerrorrange, CONVERT(VARCHAR(20),s.AddTime,20) AS addtime FROM dbo.XG_Class s INNER JOIN dbo.XG_Route r ON r.RouteId = s.RouteID
r.routeid=#{routeid}
and s.classid=#{classid}
