mapper.select:
select distinct t.*,h.holderName "operatorName",ty.typeName , count(1) over(partition by t.fid ) sumVal from ZC_Goods t left join HolderData h on t.operator = h.holderNo left join ZC_GoodsType ty on t.type = ty.fid inner join ZC_InStoreGoodsRecord gs on t.fid=gs.goodsID where 1=1
and CONVERT(varchar(100), gs.inStoreDate, 23) >= #{startDate}
and CONVERT(varchar(100), gs.inStoreDate, 23) <= #{endDate}

select count(1) from ( select distinct t.*,h.holderName "operatorName",ty.typeName , count(1) over(partition by t.fid ) sumVal from ZC_Goods t left join HolderData h on t.operator = h.holderNo left join ZC_GoodsType ty on t.type = ty.fid inner join ZC_InStoreGoodsRecord gs on t.fid=gs.goodsID where 1=1
and CONVERT(varchar(100), gs.inStoreDate, 23) >= #{startDate}
and CONVERT(varchar(100), gs.inStoreDate, 23) <= #{endDate}
) aa


	select  t.*,b.bill,ty.typeName,mf.name "manufacturerName",s.name "supplierName",g.specs,g.size,g.brand,ss.storeName,t.filePath from ZC_InStoreGoodsRecord t
		left join ZC_InStore b on t.inStoreID = b.fid
		left join ZC_GoodsType ty on t.type=ty.fid
		left join ZC_Manufacture mf on t.manufacturerID = mf.fid
		left join ZC_Supplier s on t.supplierID = s.fid
		left join ZC_Goods g on t.goodsID = g.fid
		left join ZC_Store ss on t.storeID = ss.fid
	where t.goodsID = #{goodsID}

 		and CONVERT(varchar(100), t.inStoreDate, 23) >= #{startDate}



 		and CONVERT(varchar(100), t.inStoreDate, 23) <= #{endDate}

	select count(1) from ZC_InStoreGoodsRecord t
		left join ZC_InStore b on t.inStoreID = b.fid
		left join ZC_GoodsType ty on t.type=ty.fid
		left join ZC_Manufacture mf on t.manufacturerID = mf.fid
		left join ZC_Supplier s on t.supplierID = s.fid
		left join ZC_Goods g on t.goodsID = g.fid
		left join ZC_Store ss on t.storeID = ss.fid
