 		and CONVERT(varchar(100), t.inStoreDate, 23) >= #{startDate}



 		and CONVERT(varchar(100), t.inStoreDate, 23) <= #{endDate}

select distinct t.*,h.holderName "operatorName",ty.typeName , count(1) over(partition by t.fid ) sumVal from ZC_Goods t left join HolderData h on t.operator = h.holderNo left join ZC_GoodsType ty on t.type = ty.fid inner join ZC_OutStoreGoods gs on t.fid=gs.goodsID where 1=1
and CONVERT(varchar(100), t.outStoreDate, 23) >= #{startDate}
and CONVERT(varchar(100), t.outStoreDate, 23) <= #{endDate}

select count(1) from ( select distinct t.*,h.holderName "operatorName",ty.typeName , count(1) over(partition by t.fid ) sumVal from ZC_Goods t left join HolderData h on t.operator = h.holderNo left join ZC_GoodsType ty on t.type = ty.fid inner join ZC_OutStoreGoods gs on t.fid=gs.goodsID where 1=1
and CONVERT(varchar(100), t.outStoreDate, 23) >= #{startDate}
and CONVERT(varchar(100), t.outStoreDate, 23) <= #{endDate}
) aa

select t.*,h1.holderName "operatorName",h2.holderName "approverName",h3.holderName "receiverName",d.departmentName "receiveUnitName" , g.specs,g.size,g.brand,ss.storeName,m.name "manufacturer",sp.name "supplierName",t.filePath from ZC_OutStoreGoods t left join ZC_OutStore o on t.outStoreID = o.fid left join HolderData h1 on o.operator = h1.holderNo left join HolderData h2 on o.approver = h2.holderNo left join HolderData h3 on o.receiver = h3.holderNo left join DepartmentData d on o.receiveUnit = d.departmentNo left join ZC_Goods g on t.goodsID = g.fid left join ZC_Store ss on t.storeID = ss.fid left join ZC_Manufacture m on t.manufacturerID = m.fid left join ZC_Supplier sp on t.supplierID = sp.fid where t.goodsID=#{goodsID}
and CONVERT(varchar(100), t.outStoreDate, 23) >= #{startDate}
and CONVERT(varchar(100), t.outStoreDate, 23) <= #{endDate}

select count(1) from ZC_OutStoreGoods t left join ZC_OutStore o on t.outStoreID = o.fid left join HolderData h1 on o.operator = h1.holderNo left join HolderData h2 on o.approver = h2.holderNo left join HolderData h3 on o.receiver = h3.holderNo left join DepartmentData d on o.receiveUnit = d.departmentNo left join ZC_Goods g on t.goodsID = g.fid left join ZC_Store ss on t.storeID = ss.fid left join ZC_Manufacture m on t.manufacturerID = m.fid left join ZC_Supplier sp on t.supplierID = sp.fid where t.goodsID=#{goodsID}
and CONVERT(varchar(100), t.outStoreDate, 23) >= #{startDate}
and CONVERT(varchar(100), t.outStoreDate, 23) <= #{endDate}

select distinct t.*,h.holderName "operatorName",ty.typeName , count(1) over(partition by t.fid ) sumVal from ZC_Goods t left join HolderData h on t.operator = h.holderNo left join ZC_GoodsType ty on t.type = ty.fid inner join ZC_BorrowRecordGoods gs on t.fid=gs.goodsID where 1=1
and CONVERT(varchar(100), gs.operateDate, 23) >= #{startDate}
and CONVERT(varchar(100), gs.operateDate, 23) <= #{endDate}

select count(1) from ( select distinct t.*,h.holderName "operatorName",ty.typeName , count(1) over(partition by t.fid ) sumVal from ZC_Goods t left join HolderData h on t.operator = h.holderNo left join ZC_GoodsType ty on t.type = ty.fid inner join ZC_BorrowRecordGoods gs on t.fid=gs.goodsID where 1=1
and CONVERT(varchar(100), gs.operateDate, 23) >= #{startDate}
and CONVERT(varchar(100), gs.operateDate, 23) <= #{endDate}
) aa

select t.*,r.bill,d.departmentName "borrowUnitName",h.holderName "borrowPerName", r.phone,CONVERT(varchar(100),r.borrowDate, 23) borrowDate, CONVERT(varchar(100),r.startDate, 23) "borrowStartDate", CONVERT(varchar(100),r.endDate, 23) "borrowEndDate", CONVERT(varchar(100),r.operateDate, 23) borrowOperateDate, h2.holderName "borrowOperatorName",g.specs,g.brand,g.size,ss.storeName,r.remark "mremark",t.filePath from ZC_BorrowRecordGoods t inner join ZC_BorrowRecord r on t.borrowID = r.fid left join DepartmentData d on r.borrowUnit = d.departmentNo left join HolderData h on r.borrowPer = h.holderNo left join HolderData h2 on r.operator = h2.HolderNo left join ZC_Goods g on t.goodsID = g.fid left join ZC_Store ss on t.storeID = ss.fid where 1=1
and CONVERT(varchar(100), t.operateDate, 23) >= #{startDate}
and CONVERT(varchar(100), t.operateDate, 23) <= #{endDate}

select count(1) from ZC_BorrowRecordGoods t inner join ZC_BorrowRecord r on t.borrowID = r.fid left join DepartmentData d on r.borrowUnit = d.departmentNo left join HolderData h on r.borrowPer = h.holderNo left join HolderData h2 on r.operator = h2.HolderNo left join ZC_Goods g on t.goodsID = g.fid left join ZC_Store ss on t.storeID = ss.fid where 1=1
and CONVERT(varchar(100), t.operateDate, 23) >= #{startDate}
and CONVERT(varchar(100), t.operateDate, 23) <= #{endDate}
